rect(w, h, x, y) = Shape(x .+ [0, w, w, 0, 0], y .+ [0, 0, h, h, 0])gray(pct) = RGB(pct, pct, pct)function windowrange(dim, denom)    range(0, 1, length = max(3, round(Int, dim/denom)))[2:end - 1]endfor k in 1:50    w, h, x, y = 0.1rand() + 0.05, 0.8rand() + 0.3, rand(), 0.0    shape = rect(w, h, x, y)    graypct = 0.3rand() + 0.3    plot!(shape, c = gray(graypct))    # windows    I = windowrange(w, 0.015)    J = windowrange(h, 0.04)    pts = vec([(Float64(x + w * i), Float64(y + h * j)) for i in I, j in J])    windowcolors = Symbol[rand() < 0.2 ? :yellow : :black for i in 1:length(pts)]    scatter!(pts, marker = (stroke(0), :rect, windowcolors))endplt